package com.pinkulu;

public class Util {
    public static String replacer(String text){return text.replaceAll("§r", "");}
    public static String replacee(String text){return text.replaceAll("§e", "");}
    public static String replace7(String text){return text.replaceAll("§7", "");}
    public static String replacef(String text){return text.replaceAll("§f", "");}
    public static String replacething(String text){return text.replaceFirst(": ", "§f: " + pingPlayer.Cformat );}
    public static String replaceformat(String text){return text.replaceAll("&", "§");}
    public static String replaceemoji1(String text){return text.replaceAll("❤", "❤" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji2(String text){return text.replaceAll("✮", "✮" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji3(String text){return text.replaceAll("✔", "✔" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji4(String text){return text.replaceAll("✖", "✖" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji5(String text){return text.replaceAll("☕", "☕" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji6(String text){return text.replaceAll("➜", "➜" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji7(String text){return text.replaceAll("¯_ツ_/¯", "¯\\\\_(ツ)_/¯" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji8(String text){return text.replaceAll("╯°□°╯︵ ┻━┻", "\\(╯°□°）╯︵ ┻━┻" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji9(String text){return text.replaceAll(" ﾟ◡ﾟ/", "( ﾟ◡ﾟ)/" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji10(String text){return text.replaceAll("123", "123" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji11(String text){return text.replaceAll("☉_☉", "☉_☉" +replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji12(String text){return text.replaceAll("✎...", "✎..." + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji13(String text){return text.replaceAll("√π+x=L", "\\\\√(π+x)=L" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji14(String text){return text.replaceAll("@'-'", "" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji15(String text){return text.replaceAll("0.o", "(0.o?)" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji16(String text){return text.replaceAll("༼つ◕_◕༽つ", "༼つ◕_◕༽つ" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji17(String text){return text.replaceAll("'-'⊃━☆ﾟ.*･｡ﾟ", "('-')⊃━☆ﾟ.*･｡ﾟ" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji18(String text){return text.replaceAll("⚔", "⚔" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji19(String text){return text.replaceAll("✌", "✌" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji20(String text){return text.replaceAll("oof", "OOF" + replaceformat(pingPlayer.Cformat));}
    public static String replaceemoji21(String text){return text.replaceAll("puffer", "puffer" + replaceformat(pingPlayer.Cformat));}

}
